mapper.select:
select title
from mellow_communitytype
where tno = #{tno}

select bno, title, content, writer, regdate, viewcnt, image, replycnt
from mellow_board
where bno = #{bno}

select count(bno)
from mellow_board
where tno=#{tno}

mapper.update:

update mellow_board set title=#{title} , content=#{content}

	,image=#{image}

where bno=#{bno}
